(function($){ 
    $.extend({  
        loadingShow: function({sizes,imageUrl}) {//sizes设置图片大小，imageUrl设置图片路径  
            var sizes = sizes || '200px';
			var loadingDiv ='<div id="loading" style="width:100%;height:100%;position: fixed;left: 0;top: 0;z-index:1001;background: rgba(0, 0, 0, 0.5);">'
                                +'<div style="position: absolute;left: 0;top: 0;right: 0;bottom: 0;width:'+sizes+';height:'+sizes+';margin: auto;background:url('+imageUrl+') center no-repeat;background-size: cover;"></div>'
                            +'</div>';
            $('body').append(loadingDiv);  
            $('html,body').css({"height":"100%","overflow":"hidden"});
		},  
        loadingHide: function() {  
            $('#loading').remove();
            $('html,body').css({"height":"auto","overflow":"auto"});
        }  
    })
})($);
			